mapper.update:
update alm_app_installs set
  install_id = #{installId, jdbcType=VARCHAR},
  updated_at = #{now, jdbcType=BIGINT}
where
  alm_id = #{almId, jdbcType=VARCHAR}
  and owner_id = #{ownerId, jdbcType=VARCHAR}

mapper.delete:
delete from alm_app_installs
where
  alm_id = #{almId, jdbcType=VARCHAR}
  and owner_id = #{ownerId, jdbcType=VARCHAR}
