mapper.select:
SELECT 
FROM projects p
INNER JOIN snapshots s on s.project_id=p.id and s.islast=${_true}

  AND p.enabled=${_true}
  AND p.kee=#{key}

SELECT 
FROM projects p
INNER JOIN snapshots s on s.project_id=p.id and s.islast=${_true}

  AND p.enabled=${_true}
  AND p.id=#{id}

SELECT count(p.id)
FROM projects p

  AND p.enabled=${_true}
  AND p.id=#{id}
