mapper.select:
select 
from issues i
where i.id=#{id}

select 
from issues i
where i.uuid=#{uuid}

select  from issues i

  , projects p, snapshots root, snapshots s



    and p.enabled=${_true}
    and p.kee in
    #{componentKey}

    and root.project_id=p.id
    and root.islast=${_true}
    and (s.id=root.id or (s.root_snapshot_id=root.id and


        s.path LIKE root.path + CAST(root.id AS varchar(15)) + '.%'


        s.path LIKE root.path || root.id || '.%'


    ))
    and s.islast=${_true}
    and i.resource_id=s.project_id


    and i.uuid in
    #{key}



    and i.severity in
    #{severity}



    and i.status in
    #{stat}



    and i.resolution in
    #{resolution}



    and i.user_login in
    #{userLogin}



    and i.assignee_login in
    #{assigneeLogin}
