mapper.sql:
from issues i
inner join () authorizedProjects on authorizedProjects.root_project_id=i.root_component_id

  inner join () components on components.project_id=i.component_id


  inner join projects project_component on project_component.id=i.component_id and project_component.enabled=${_true} and project_component.kee in
  #{component}



  inner join rules r on r.id=i.rule_id and (
  r.plugin_name=#{rule.repository} and r.plugin_rule_key=#{rule.rule})



    and i.kee in
    #{key}



    and i.severity in
    #{severity}



    and i.status in
    #{status}



    and i.resolution in
    #{resolution}




      and i.resolution is not null


      and i.resolution is null



    and i.reporter in
    #{reporter}



    and i.assignee in
    #{assignee}




      and i.assignee is not null


      and i.assignee is null




      and i.action_plan_key is not null


      and i.action_plan_key is null



    and i.action_plan_key in

      #{action_plan}



    and i.issue_creation_date >= #{query.createdAfter}


    and i.issue_creation_date < #{query.createdBefore}


    and i.issue_creation_date < #{query.createdBefore}
