mapper.update:
UPDATE active_rules SET
profile_id=#{profileId},
rule_id=#{ruleId},
failure_level=#{severity},
inheritance=#{inheritance}
WHERE id=#{id}

mapper.select:
SELECT 
FROM active_rules a


  AND a.id=#{id}

SELECT

FROM active_rules a


  AND qp.name = #{profile}
  AND qp.language = #{language}
  AND r.plugin_rule_key = #{rule}
  AND r.plugin_name = #{repository}

SELECT 
FROM active_rules a

where qp.name = #{name}
AND qp.language = #{lang}

SELECT

FROM active_rules a

WHERE a.rule_id=#{ruleId}

SELECT 
FROM active_rules a


  AND a.profile_id=#{profileId}

mapper.update:
UPDATE active_rule_parameters SET
active_rule_id=#{activeRuleId},
rules_parameter_id=#{rulesParameterId},
rules_parameter_key=#{key},
value=#{value}
WHERE id=#{id}

mapper.select:
SELECT 
FROM active_rule_parameters p

  AND p.active_rule_id=#{activeRuleId}
  AND p.rules_parameter_key=#{key}
