mapper.select:
SELECT 
FROM rules_parameters

  AND rule_id=#{ruleId}
  AND name=#{key}

mapper.update:
UPDATE rules_parameters SET
  name=#{name},
  param_type=#{type},
  default_value=#{defaultValue},
  description=#{description}
WHERE id=#{id}

UPDATE rules_rule_tags
SET tag_type=#{type}
WHERE id=#{id}
