mapper.select:
select 
from characteristics c

  and c.enabled=${_true}

select from characteristics c
and c.parent_id is null and c.enabled=${_true}
order by characteristic_order asc

select 
from characteristics c

  and c.parent_id=#{parent_id}
  and c.enabled=${_true}

select 
from characteristics c

  and c.kee=#{key}
  and c.enabled=${_true}

select 
from characteristics c

  and c.id=#{id}
  and c.enabled=${_true}

select 
from characteristics c

  and c.name=#{name}
  and c.enabled=${_true}

select 
from characteristics c

  and c.characteristic_order>#{order}
  and c.parent_id is null
  and c.enabled=${_true}

order by characteristic_order asc

select 
from characteristics c

  and c.characteristic_order<#{order}
  and c.parent_id is null
  and c.enabled=${_true}

order by characteristic_order asc
